DROP SCHEMA IF EXISTS `test`;
CREATE SCHEMA `test` DEFAULT CHARACTER SET utf8;
USE `test`;

CREATE TABLE `ria_marks` (
  `mark_id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(45) CHARACTER SET utf8 NOT NULL,
  PRIMARY KEY (`mark_id`),
  UNIQUE KEY `name_UNIQUE` (`name`)
) 
ENGINE=InnoDB 
DEFAULT CHARSET=utf8;

CREATE TABLE `ria_types` (
  `type_id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` varchar(45) CHARACTER SET utf8 NOT NULL,
  PRIMARY KEY (`type_id`),
  UNIQUE KEY `name_UNIQUE` (`name`)
)
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8;

CREATE TABLE `ria_models` (
  `model_id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
  `mark_id` INT UNSIGNED NOT NULL,
  `name` VARCHAR(45) NOT NULL,
  PRIMARY KEY (`model_id`),
  INDEX `mark_id_IDX` (`mark_id` ASC),
  CONSTRAINT `mark_id` FOREIGN KEY (`mark_id`) REFERENCES `ria_marks` (`mark_id`) ON DELETE CASCADE ON UPDATE CASCADE
)
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8;

CREATE TABLE `ria_model_types` (
  `model_type_id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
  `model_id` INT UNSIGNED NOT NULL,
  `type_id` INT UNSIGNED NOT NULL,
  PRIMARY KEY (`model_type_id`),
  CONSTRAINT `model_id` FOREIGN KEY (`model_id`) REFERENCES `ria_models` (`model_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `type_id` FOREIGN KEY (`type_id`) REFERENCES `ria_types` (`type_id`) ON DELETE CASCADE ON UPDATE CASCADE
)
ENGINE = InnoDB
DEFAULT CHARACTER SET = utf8;

CREATE TABLE `ria_users` (
  `user_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(45) NOT NULL,
  `email` varchar(45) NOT NULL,
  `phone` int(10) unsigned zerofill NOT NULL,
  `register_date` datetime NOT NULL,
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `email_UNIQUE` (`email`),
  UNIQUE KEY `phone_UNIQUE` (`phone`)
) 
ENGINE=InnoDB 
DEFAULT CHARSET=utf8;

CREATE TABLE `ria_currencies` (
  `currency_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `iso` char(3) CHARACTER SET utf8 NOT NULL,
  PRIMARY KEY (`currency_id`),
  UNIQUE KEY `iso_UNIQUE` (`iso`)
) 
ENGINE=InnoDB 
DEFAULT CHARSET=utf8;

CREATE TABLE `ria_cities` (
  `city_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(45) CHARACTER SET utf8 NOT NULL,
  PRIMARY KEY (`city_id`),
  UNIQUE KEY `name_UNIQUE` (`name`)
) 
ENGINE=InnoDB 
DEFAULT CHARSET=utf8;

CREATE TABLE `ria_auto` (
  `auto_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int(10) unsigned NOT NULL,
  `city_id` int(10) unsigned NOT NULL,
  `mark_id` int(10) unsigned NOT NULL,
  `model_id` int(10) unsigned NOT NULL,
  `version` varchar(45) DEFAULT '',
  `price` int(10) unsigned NOT NULL,
  `currency_id` int(10) unsigned NOT NULL,
  PRIMARY KEY (`auto_id`),
  KEY `version` (`version`) USING BTREE,
  KEY `price` (`price`),
  KEY `mark_id_idx` (`mark_id`),
  KEY `model_id_idx` (`model_id`),
  KEY `user_id_fk_idx` (`user_id`),
  KEY `currency_id_fk_idx` (`currency_id`),
  CONSTRAINT `mark_id_fk` FOREIGN KEY (`mark_id`) REFERENCES `ria_marks` (`mark_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `model_id_fk` FOREIGN KEY (`model_id`) REFERENCES `ria_models` (`model_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `user_id_fk` FOREIGN KEY (`user_id`) REFERENCES `ria_users` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `city_id_fk` FOREIGN KEY (`city_id`) REFERENCES `ria_cities` (`city_id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `currency_id_fk` FOREIGN KEY (`currency_id`) REFERENCES `ria_currencies` (`currency_id`) ON DELETE CASCADE ON UPDATE CASCADE
)
ENGINE=InnoDB 
DEFAULT CHARSET=utf8;

